mapper.select:
select id, password
from user
where phone = #{phone}

mapper.insert:
insert into user (username, avatar_url,
  `password`, phone, create_time, 
  update_time, introduction, sex
  )
values ( #{username,jdbcType=VARCHAR}, #{avatarUrl,jdbcType=VARCHAR},
